import React from 'react';
import ReactDOM from 'react-dom';
import Button from "./pages/Home";

import App from './App';

class Home extends React.Component{

  state={
      matiere: [
          {id:1, nom:"Maths"},
          {id:2, nom:"Chimie"},
          {id:3, nom:"Physique"}
      ]
    };

    handleChange = (id) => {
      const matieres = [...this.state.matieres];
      const fiche = matieres.findIndex(matiere => matiere.id === id);
  
      matieres.map(matiere => {matiere.actuel = false;});
      const matiere = this.state.matieres[fiche];
      matiere.actuel = true;
      matieres[fiche]=matiere;
      this.setState({matieres});
    };



  render() {
      return (
      
        <div>
          <article class="button">
          {this.state.matieres.map(matiere => (
          <Button 
          details={matiere}
          change={this.handleChange}
          />
          ))}
          </article>
      </div>
      )
  }
}



ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
